import React, { useRef } from 'react';
import './DetailPage.css';

const appScreens = [
  '/app1.png',
  '/app2.png',
  '/app3.png',
];

function DetailPage() {
  const sliderRef = useRef();

  // 세로 스크롤을 가로 스크롤로 변환
  const handleWheel = (e) => {
    if (sliderRef.current) {
      sliderRef.current.scrollLeft += e.deltaY;
    }
  };

  return (
    <div className="detail-page">
      <h1>U-TEED 앱 소개</h1>
      <div className="mockup-container">
        <img src="/mockup.png" alt="앱 목업" className="mockup-img" />
        <div className="mockup-screen" onWheel={handleWheel}>
          <div className="mockup-slider" ref={sliderRef}>
            {appScreens.map((src, idx) => (
              <img key={idx} src={src} alt={`앱 화면 ${idx+1}`} className="mockup-app-img" />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default DetailPage;
